mapper.select:
SELECT

	count(nMemberSeq) nMemberSeq


	nMemberSeq, sId, sName, sNickName, sEmail, sBirthDate, dtJoinDate, sPassword, nSex

FROM tMember
WHERE 1=1

	AND nMemberSeq = #{memberSeq}


	AND sId = #{id}


	AND sEmail = #{email}


	AND sName = #{name}


	AND sNickName = #{nickName}


	AND sBirthDate = #{birthDate}
